import axios from 'axios';

const api = axios.create({
  baseURL: 'http://192.168.0.18:3000/', // Actualiza la dirección IP aquí
});

// Request interceptor
api.interceptors.request.use(
  (config) => {
    // Si no estás utilizando autenticación, puedes eliminar este bloque
    const user = AuthService.getCurrentUser();
    if (user != null && user.token != null) {
      config.headers.Authorization = `Bearer ${user.token}`;
    }
    return config;
  },
  (err) => {
    return Promise.reject(err);
  },
);

// Response interceptor
api.interceptors.response.use(
  (response) => {
    // Puedes ajustar o eliminar la lógica de respuesta según sea necesario
    return response;
  },
  (err) => {
    if (err.response) {
      if (err.response.status === 401) {
        // Manejo de errores de autenticación, si es necesario
      } else if (err.response.status === 405 || err.response.status === 500) {
        // Manejo de otros errores
      }
    } else {
      // Manejo de errores de conexión
    }
    return Promise.reject(err);
  },
);

export { api };
